go:-
    statistics(runtime,_),
    top,
    statistics(runtime,[_,Y]),
    write('time : '), write(Y), nl.
    
top:-
    graph( Nodes, Edges),
    length(Nodes,N),
    hc(N,Edges).

make_domains([],_,_,_).
make_domains([X|Y],Node,Edges,N) :-
	findall(Z, member([Node,Z],Edges),L1),
	sort(L1,List),
	make_variable_domain(X,List,N),
	Node1 is Node+1,
	make_domains(Y,Node1,Edges,N).
make_variable_domain(Var,Successors,Size) :-
	Var in 1..Size,
	make_full_list(Size,Full),
	remove_elements(Successors,Full,Left),
	remove_constraints(Left,Var).

make_full_list(0,[]).
make_full_list(N,[N|Rest]) :- 
	N > 0,
	N1 is N-1,
	make_full_list(N1,Rest).

remove_elements([],R,R).
remove_elements([A|B],Current,Left) :-
	delete(Current,A,Current1),
	remove_elements(B,Current1,Left).

remove_constraints([],_).
remove_constraints([A|B],Var) :-
	Var #\= A,
	remove_constraints(B,Var).
	
hc( N, Edges ) :-
   length(Path, N),
   domain( Path, 1, N),
   make_domains(Path,1, Edges,N),
   circuit( Path ),
   labeling([ff], Path),
%   labeling_ff_count_backtrack(Path),
   !,
   write(Path),nl.

delete([],X,[]).
delete([X|Xs],X,Xs):-!.
delete([X|Xs],Y,[X|Ys]):-
    delete(Xs,Y,Ys).

